package com.alpha.eb2.session45;

public class PriValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Integer obj=10;
		int i=obj.intValue();
		System.out.println(i);
		short s=obj.shortValue();
		System.out.println(s);
		byte b=obj.byteValue();
		System.out.println(b);
		float f=obj.floatValue();
		System.out.println(f);
		Boolean obj2=true;
		boolean b1=obj2.booleanValue();
		System.out.println(b1);
		Character obj3='a';
		char c=obj3.charValue();
		System.out.println(c);
		Long obj4=10l;
		long l=obj4.longValue();
		System.out.println(l);
		int i2=obj4.intValue();
		System.out.println(i2);
		byte b2=obj4.byteValue();
		System.out.println(b2);
		short s2=obj4.shortValue();
		System.out.println(s2);
		
		float f2=obj4.floatValue();
		System.out.println(f2);
		double d2=obj4.doubleValue();
		System.out.println(d2);
		

	}

}
